import {LoginForm} from './Coponents/LoginForm'
import { useState } from 'react';

import {BrowserRouter as Router, Route, Switch, Link} from "react-router-dom";

function App() {
  const adminUser = {
  email: "admin@admin.com",
  password: "admin123"
  }
  const [user, setUser] = useState({name:"",email:""});
  const [error, setError] = useState("");

  const Login = (details) => {
     console.log(details);

     if (details.email == adminUser.email && details.password == adminUser.password){
       console.log("Logged in");
       setUser({
         name: details.name,
         email: details.email
       });
     } else {
       console.log("Details do no match!");
       setError("Details do no match!");
     }
   }

   const Logout = () => {
      console.log("Logout");
      setUser({name: "", email: "", password: ""});
   }

   const Register = () => {
    console.log("Registered")
   }
  return (
    <Router>
      <div className="App">
        <style>{'body {backgroun-color: #ebe3d8 ;}'}</style>
        <div className='Home'>
          <Switch>
          {/* {(user.email != "") ? (
                  <div className='welcome'>
                    <h2>Welcome, <span>{user.name}</span></h2>
                    <button onClick={Logout}>Logout</button>
                  </div>
              ) : (
                <LoginForm Login={Login} error={error} />
              )}
             */}


            <Route path="/">
              {(user.email != "") ? (

                     <div className='welcome'>
                        {/* <h2>Welcome, <span>{user.name}</span></h2>
                        <button onClick={Logout} href="/Login">Logout</button> */}
                         <Link to="/home"></Link>
                         <NavBar Logout={Logout} name={user.name}/>
                         <Dashboard />

                      </div>
                ) : (
                  <LoginForm Login={Login} error={error} />
                )}
            </Route>
            <Route path="/home">
              <NavBar Logout={Logout} />
              <Dashboard />
            </Route>

          </Switch>
        </div>

        {/* <NavBar />
        <Banner />
        <ProductsCarousel />
        <Footer /> */}
      </div>
    </Router>
  );
}

export default App;
