import styled from 'styled-components';


export const Heading = styled.p`
    color: #5C4033;
    font-size: 24px;
    margin: 4px;
    font-weight: bold;
`;

export const Container = styled.div`
	display: flex;
	flex-direction: column;
	justify-content: center;
	max-width: 1000px;
	margin: 0 auto;
	/* background: red; */
`

export const Column = styled.div`
display: flex;
flex-direction: column;
text-align: left;
margin-left: 60px;
`;

export const Row = styled.div`
    display: grid;
    grid-template-columns: repeat(auto-fill,
                            minmax(185px, 1fr));
    grid-gap: 20px;

    @media (max-width: 320px) {
        grid-template-columns: repeat(auto-fill,
                            minmax(200px, 1fr));
}
`;

export const FooterLink = styled.a`
    color: #5C4033;
    font-size: 18px;
    text-decoration: none;
    margin-top: 10%;
    padding-top: 40px;
    padding: 10px;
    position: absolute;
  z-index: -1;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;

    &:hover {
        color: black;
        transition: 200ms ease-in;
    }
`;
