import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

import {NavBar} from './Coponents/NavBar'
import {Banner} from './Coponents/Banner'
import {ProductsCarousel} from './Coponents/ProductsCarousel'
import {Footer} from './Coponents/Footer'
import {Dashboard} from './Coponents/Dashboard'
import { RegisterForm } from './Coponents/RegisterForm';
import {LoginForm} from './Coponents/LoginForm'
import { useState } from 'react';
import { withRouter } from 'react-router';
import {BrowserRouter as Router, Route, Switch, Link,useHistory } from "react-router-dom";

function App() {

  const adminUser = {
  email: "admin@admin.com",
  password: "admin123"
  }
  const [user, setUser] = useState({name:"",email:""});
  const [error, setError] = useState("");

  const Login = (details) => {
     console.log(details);

     if (details.email == adminUser.email && details.password == adminUser.password){
       console.log("Logged in");
       setUser({
         name: details.name,
         email: details.email
       });
     } else {
       console.log("Details do no match!");
       setError("Details do no match!");
     }
   }

   const Logout = () => {
      console.log("Logout");
      setUser({name: "", email: "", password: ""});
   }

   const history = useHistory();

   const SwitchToLogin = () =>{
    // history.push("/Login");
    this.props.router.push('/Login');
    // <Link to="/Login" />
   }

   const SwitchToRegister = () => {
    // history.push("/Register");
    this.props.router.push('/Register');
    // <Link to="/Register" />
   }

  return (
    <Router>
      <div className="App">
        <style>{'body {backgroun-color: #ebe3d8 ;}'}</style>
        <div className='Home'>
          <Switch>
          {/* {(user.email != "") ? (
                  <div className='welcome'>
                    <h2>Welcome, <span>{user.name}</span></h2>
                    <button onClick={Logout}>Logout</button>
                  </div>
              ) : (
                <LoginForm Login={Login} error={error} />
              )}
             */}
            {/* <Route path="/">
              <button className="button" onClick={SwitchToRegister} >Register</button><br />
              <button className="button" onClick={SwitchToLogin} >Login</button>
            </Route> */}
            <Route path="/">
              {(user.email != "") ? (

                     <div className='welcome'>
                        {/* <h2>Welcome, <span>{user.name}</span></h2>
                        <button onClick={Logout} href="/Login">Logout</button> */}
                         <Link to="/home"></Link>
                         <NavBar Logout={Logout} name={user.name}/>
                         <Dashboard />

                      </div>
                ) : (
                  <div>
                    <LoginForm Login={Login} error={error} />
                    {/* <button className="button" onClick={handleClick}>Sign up</button> */}
                  </div>
                )}
            </Route>
            <Route path="/home">
              <NavBar Logout={Logout} />
              <Dashboard />
            </Route>
            <Route path="/Register">
              <RegisterForm Register={Register}/>
            </Route>
          </Switch>
        </div>

        {/* <NavBar />
        <Banner />
        <ProductsCarousel />
        <Footer /> */}
      </div>
    </Router>
  );
}

export default App;
