import { useState, useEffect } from "react";
import { Navbar, Container, Nav} from "react-bootstrap"
import logo from '../Assets/logo.svg';

export const NavBar = () => {
    const [activeLink, setActiveLink] = useState('home');
    const [scrolled, seScrolled] = useState(false);    

    useEffect( () => {
        const onScroll = () => {
            if (window.scrollY > 50) {
                seScrolled(true);
            } else {
                seScrolled(false);
            }
        }
        window.addEventListener("scroll", onScroll);

        return () => window.removeEventListener("scroll", onScroll);
    }, [])

    const onUpdateActiveLink = (value) =>{
        setActiveLink(value);
    }

    return (
        <Navbar bg="" expand="lg" className={scrolled ? "scrolled" : ""}>
            <Container className="NavBar">
                <Navbar.Brand href="#home">
                    <img className="logo" src={logo} alt="Logo" />
                </Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" >
                    <span className="navbar-toggler-icon"></span>
                </Navbar.Toggle>
                <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="ms-auto">
                    <Nav.Link href="#theway" className={activeLink === 'theway'?'active navbar-link':'navbar-link'} onClick={() => onUpdateActiveLink('theway')}>The Way</Nav.Link>
                    <Nav.Link href="#thewhy" className={activeLink === 'thewhy'?'active navbar-link':'navbar-link'} onClick={() => onUpdateActiveLink('thewhy')}>The Why</Nav.Link>
                    <Nav.Link href="#theus" className={activeLink === 'theus'?'active navbar-link':'navbar-link'} onClick={() => onUpdateActiveLink('theus')}>The Us</Nav.Link>
                    <Nav.Link href="#thehow" className={activeLink === 'thehow'?'active navbar-link':'navbar-link'} onClick={() => onUpdateActiveLink('thehow')}>The How</Nav.Link>
                    {/* <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                    <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                    <NavDropdown.Item href="#action/3.2">
                        Another action
                    </NavDropdown.Item>
                    <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                    <NavDropdown.Divider />
                    <NavDropdown.Item href="#action/3.4">
                        Separated link
                    </NavDropdown.Item>
                    </NavDropdown> */}
                </Nav>
                {/*<span className="navbar-text">
                    <div className="social-icon">
                        <a href="#"><img src={''} alt="" /></a>
                        <a href="#"><img src={''} alt="" /></a>
                        <a href="#"><img src={''} alt="" /></a>
                    </div>
                    <button className="vvd" onClick={() => console.log('connect')}><span>Let's connect</span></button>
                </span>*/}
                </Navbar.Collapse>
            </Container>
        </Navbar>
    )
}
